/**
 * Since we want to find the position of the first character intstruction which will cause Santa
 * to visit the basement, it's mandatory to iterate through each character in the input array
 * while keeping track of the character instruction position until Santa reaches floor -1.
 */

func computeInstruction(instruction: Character, var floor: Int) -> Int{
    switch instruction {
    case "(":
        floor += 1
    case ")":
        floor -= 1
    default:
        break;
    }
    return floor
}

var currentFloor = 0

for (index, instruction) in instructions.characters.enumerate(){
    currentFloor = computeInstruction(instruction, floor: currentFloor)
    if currentFloor == -1 {
        // index of 1 instead of 0 for human readability
        let characterPosition = index + 1
        print("The character position of the instruction that will take Santa into the basement is: \(characterPosition)")
        //prints 1783
        break;
    }
}

